import React,{userState} from 'react';
import Details from './components/UserDetails/Details';
import UserList from './components/UserDetails/UserList';

function App() {
  const[enteredData,setEnteredData]=userState([])
  const addHandler=(uName,uAge)=>{
setEnteredData((prevList)=>{
   return[...prevList,{name:uName,age:uAge}]
})
  }
  return (
    <div>
     <Details onAddHandler={addHandler}/>
     <UserList user={enteredData}/>
    </div>
  );
}

export default App;
